/** @format */

import { Col, Container, Row } from "react-bootstrap";
import "bootstrap/dist/css/bootstrap.min.css";
import { Alert } from "bootstrap";

function App() {
  const person = [
    { id: "1", name: "احمد", time: "5 مساء", img: "p1.jpg" },
    // { id: "2", name: "خالد", time: "3 عصر", img: "p1.jpg" },
    // { id: "3", name: "صبحي", time: "8 صباح", img: "p1.jpg" },
    // { id: "4", name: "محسن", time: "2 عصر", img: "p1.jpg" },
    // { id: "5", name: "تامر", time: "12 ظهر", img: "p1.jpg" },
  ];
  // person = null;

  return (
    <div className="color-body font">
      <Container className="py-4">
        <Row className="justify-content-center py-2">
          <Col sm="8">لديلك {person.length} مواعيد</Col>
        </Row>
        <Row className="justify-content-center">
          <Col sm="8">
            <div className="rectangle p-2">
              {person.length ? (person).map((e)=>{
                return (
                  <div className="d-flex  border-bottom my-3">
                  <img className="img-avatar" alt="" src={e.img} />
                  <div className="px-3 ">
                    <p className="fs-5 d-inline">{e.name}</p>
                    <p className="fs-6">{e.time}</p>
                  </div>
                </div>
                )
              }) :<h2>لا يوجد مواعيد اليوم</h2>}
            </div>
          </Col>
        </Row>
        <Row className="justify-content-center py-2">
          <Col sm="8" className="d-flex justify-content-between">
            <button className="btn-style p-2">مسح الكل</button>
            <button className="btn-style p-2">عرض الكل</button>
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
