export const myReduser = (state = {fName:'ayman'} ,action)=>{
    if(action.type === 'first'){
        return {fName : state.fName}
    }
    if(action.type === 'secnd'){
        return {fName : 'mugahed'}
    }
    if(action.type === 'theerd'){
        return  {fName : 'alaozry'}
    }
    return state
}
