import React from 'react'
import { useDispatch, useSelector } from 'react-redux'

const App = () => {
  const name = useSelector((n)=>{return n.result});
  const dispatchName = useDispatch();
  console.log(name)
  return (
    <div>
      <h1>name :{name}</h1>
      <button onClick={()=>dispatchName({type:'first'})}> F</button>{' '}
      <button onClick={()=>{dispatchName({type:'secnd'})}}>S</button>{' '}
      <button onClick={()=>{dispatchName({type:'theerd'})}}>L</button>{' '}
    </div>
  )
}

export default App
