/** @format */
import React, { useState } from "react";
import { Button, Col, Container, Row } from "react-bootstrap";
import FormQuese from "./component/FormQuese";
import AccordionQuse from "./component/Accordion";
import { dataQuestion } from "./DataQuestion";

function App() {
  const [data , setData] = useState(dataQuestion);
  const addItem =()=> {
    localStorage.setItem("Items",JSON.stringify([...dataQuestion]));
    setData([...dataQuestion]);
  }
  const deleteAll =()=>{
    localStorage.removeItem("Items");
    dataQuestion.splice(0)
    setData([])
  }
  const deletItem = (item)=>{
    localStorage.setItem("Items",JSON.stringify([...dataQuestion]));
    setData([...item])
  }
  // data.map((e)=>console.log('data ' + e.id))
  return (
    <div className="color-body font">
      <Container className="justify-content-center p-4">
        <Row>
          <Col sm="4">
            <div className="fs-3 text-center py-2">أسئلة واجوبة شائعة</div>
          </Col>
          <Col sm='8'>
          <FormQuese onAdd={addItem} />
          <AccordionQuse data={data} onDeleteItem= {deletItem}/>
          {
            data.length >= 1 ? <Button className="w-100 my-1" onClick={deleteAll}>مسح الكل</Button> :null  
          }
          </Col>
        </Row>
      </Container>
    </div>
  );
}

export default App;
