export const LoginUser = (stateUser ={ state : 'login'} , action) => {
 if(action.type === 'login') {
    return {state : 'UserLogin'}
 }
 if(action.type === 'UserLogin') {
    return {state : 'UserLogout'}
 }
 return stateUser
}
