/** @format */

export const counterReducer = (state = { cont: 5 }, action) => {
    if(action.type === "+"){
        return {cont : state.cont + 1}
    }
    if(action.type === "-"){
        return {cont : state.cont - 1}
    }
    if(action.type === ""){
        return {cont : 0}
    }
    return state
};
