import React from "react";
import { useDispatch, useSelector } from "react-redux";

const App = () => {
  const DateConter = useSelector((state) => state.counter.cont);
  const DataLogin = useSelector((state) => state.login.state);
  const DispatchConter = useDispatch();
  const DispatchLogin = useDispatch();
  // const DispatchConter = useDispatch()

  // console.log(DateConter);
  console.log(DataLogin);
  return (
    <div>
      {
        DataLogin === '' || DataLogin === 'login' ? (
          <button onClick={() => DispatchLogin({ type: 'login' })}>Login</button>

        ) : (
          <button onClick={() => DispatchLogin({ type: 'logout'})}>Logout</button>
        )
      }



    </div>
  );
};

export default App;
