export const reduse = (state = '' , action) =>{
   if(action.type === 'inc'){
    return {cont :state.cont + 1}
   }
   if(action.type === 'dec'){
    return {cont :state.cont - 1}
   }
   if(action.type === 'reset'){
    return {cont :0}
   }
   return state;
}
