export const myReduser = (state = {fName:'ayman',sName:'mujahed',lName :'alaozry'} ,action)=>{
    if(action.type === 'first'){
        return state.fName 
    }
    if(action.type === 'secnd'){
        return ` ${state.sName}`
    }
    if(action.type === 'theerd'){
        return  `${state.fName} ${state.sName} ${state.lName}`
    }
    return state
}
