export const myReduser = (state = { result :'', fName:'ayman',sName:'mujahed',lName :'alaozry'} ,action)=>{
    if(action.type === 'first'){
        return {result : state.fName}
    }
    if(action.type === 'secnd'){
        return {result:`${ state.fName} ${ state.sName}`}
    }
    if(action.type === 'theerd'){
        return   {result:`${ state.fName} ${ state.sName} ${ state.lName}`}
    }
    return state
}
