/** @format */

import React, { useState } from "react";
import { Button, Col, Form, Row } from "react-bootstrap";
import { dataQuestion } from "../DataQuestion";

const FormQuese = ({onAdd}) => {
    const [qa, setQa] = useState('');
    const [an, setAn] = useState('');
    const InsetQuastion = () => {
        dataQuestion.push({id:Math.random,q:qa , a :an});
        setAn('');
        setQa('');
        onAdd()
    }
    
  return (
    <Row className="py-2">
      <Col sm="5">
        <Form.Control
          type="text"
          id="inputPassword5"
          placeholder="ادخل السوال"
          onChange={(e)=>{
            setQa(e.target.value)
          }}
        />
      </Col>
      <Col sm="5">
        <Form.Control
          type="text"
          id="inputPassword6"
          placeholder="ادخل الجواب"
          onChange={(e)=>{
            setAn(e.target.value)
          }}
        />
      </Col>
      <Col sm='2'>
      <Button className="btn btn-primary w-100" onClick={InsetQuastion}>إضافة</Button>
      </Col>
    </Row>

  );
};

export default FormQuese;
