/** @format */

import React from "react";
import { Accordion, Button } from "react-bootstrap";
import { dataQuestion } from "../DataQuestion";

const AccordionQuse = ({ data , onDeleteItem}) => {
    const dataLocal = JSON.parse(localStorage.getItem)
    const DeletItemA = (ID)=>{
      const index = dataQuestion.findIndex((e)=>e.id === ID);
        console.log(index)
      dataQuestion.splice(index,1);
      onDeleteItem(dataQuestion);
    }
  return (
    <Accordion >
      {data.length >= 1 ? (
        data.map((e,i) => {
          return (
            <Accordion.Item key={i} eventKey={e.id} className="m-1">
              <Accordion.Header>
                <div className="m-auto">{e.q}</div>
              </Accordion.Header>
              <Accordion.Body>
                <div className="d-flex justify-content-between">
                  <div className="">{e.a}</div>
                  <Button  className="btn btn-primary" onClick={()=>{DeletItemA(e.id)}}>مسح السوال </Button>
                </div>
              </Accordion.Body>
            </Accordion.Item>
          );
        })
      ) : (
        <h2 className="text-center border text-danger">لا يوجد اسئلة</h2>
      )}
    </Accordion>
  );
};

export default AccordionQuse;
