export const myReduser = (state = {fName:0} ,action)=>{
    if(action.type === 'first'){
        return {fName : state.fName}
    }
    if(action.type === 'secnd'){
        return {fName : 'muga'}
    }
    if(action.type === 'theerd'){
        return  `${state.lName}`
    }
    return state
}
